# frozen_string_literal: true

module UsersHelper
  def profile_pic(user)
    content_tag(:div, class: 'profile-pic-div') do
      if user.image.attached?
        link_to image_tag(user.image, width: '150', height: '150', class: 'profile-pic'), user
      else
        link_to image_tag('data:image/jpeg;base64,', width: '150', height: '150', class: 'profile-pic'),
                user
      end
    end
  end

  def user_profile_pic
    content_tag(:div, class: 'profile-pic-div') do
      if @user == current_user
        if @user.image.attached?
          link_to image_tag(@user.image, width: '150', height: '150', class: 'profile-pic border border-3'),
                  new_user_profile_picture_path(current_user)
        else
          link_to image_tag('data:image/jpeg;base64,', width: '150', height: '150', class: 'profile-pic border border-3'),
                  new_user_profile_picture_path(current_user)
        end
      elsif @user.image.attached?
        link_to image_tag(@user.image, width: '150', height: '150', class: 'profile-pic border border-3'),
                user_stories_path(@user)
      else
        link_to image_tag('data:image/jpeg;base64,', width: '150', height: '150', class: 'profile-pic border border-3'),
                user_stories_path(@user)
      end
    end
  end

  def public_user_btn
    if @user.account_type == 'Public'
      button_to 'Follow', followship_index_path(following_id: @user), method: :post,
                                                                      class: 'btn btn-outline-dark follow-btn'
    elsif !current_user.requests.exists?(followee_id: @user.id)
      button_to 'Request', requests_path(id: @user.id), method: :post, class: 'btn btn-outline-dark follow-btn'
    else
      button_to 'Cancel Request', request_path(id: @user.id), method: :delete,
                                                              class: 'btn btn-outline-dark follow-btn'
    end
  end

  def account_btn
    if current_user.account_type == 'Public'
      button_to 'Private', user_path(current_user), method: :put, class: 'btn btn-outline-secondary'
    elsif current_user.account_type == 'Private'
      button_to 'Public', user_path(current_user), method: :put, class: 'btn btn-outline-secondary'
    end
  end

  def story_btn
    button_to('Add Story', new_user_story_path(user_id: @user.id), method: :get, class: 'btn btn-outline-dark') +
      button_to('View Story', user_stories_path(user_id: @user.id), method: :get, class: 'btn btn-outline-dark')
  end

  def private_btn
    if @user.account_type == 'Private'
      button_to 'View Requests', requests_path, method: :get, class: 'btn btn-outline-secondary'
    end
  end
end
